import React, { useState, useEffect } from 'react';

interface CompanyCamPhoto {
  id: string;
  url: string;
  thumbnail_url: string;
  taken_at: string;
  tags: string[];
  coordinates?: {
    latitude: number;
    longitude: number;
  };
}

interface CompanyCamIntegrationProps {
  projectId: string;
}

const CompanyCamIntegration: React.FC<CompanyCamIntegrationProps> = ({ projectId }) => {
  const [photos, setPhotos] = useState<CompanyCamPhoto[]>([]);
  const [loading, setLoading] = useState(false);
  const [companyCamProjectId, setCompanyCamProjectId] = useState('');

  const syncPhotosFromCompanyCam = async () => {
    if (!companyCamProjectId) {
      alert('Please enter CompanyCam Project ID');
      return;
    }

    setLoading(true);
    try {
      const response = await fetch(`/api/projects/${projectId}/sync-companycam`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ companyCamProjectId })
      });

      if (response.ok) {
        const data = await response.json();
        setPhotos(data.photos);
        alert(`Synced ${data.photos.length} photos from CompanyCam`);
      } else {
        alert('Failed to sync photos from CompanyCam');
      }
    } catch (error) {
      alert('Error connecting to CompanyCam');
    }
    setLoading(false);
  };

  return (
    <div className="bg-white rounded-lg shadow p-6 mt-6">
      <h3 className="text-lg font-bold mb-4">CompanyCam Integration</h3>

      <div className="mb-4">
        <label className="block font-medium mb-2">CompanyCam Project ID</label>
        <div className="flex gap-2">
          <input
            type="text"
            placeholder="Enter CompanyCam project ID"
            className="flex-1 border rounded px-3 py-2"
            value={companyCamProjectId}
            onChange={(e) => setCompanyCamProjectId(e.target.value)}
          />
          <button
            onClick={syncPhotosFromCompanyCam}
            disabled={loading}
            className="bg-blue-600 text-white px-4 py-2 rounded disabled:bg-gray-400"
          >
            {loading ? 'Syncing...' : 'Sync Photos'}
          </button>
        </div>
        <p className="text-sm text-gray-500 mt-1">
          Photos will automatically sync from your CompanyCam project to this inspection
        </p>
      </div>

      {photos.length > 0 && (
        <div>
          <h4 className="font-medium mb-3">Synced Photos ({photos.length})</h4>
          <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
            {photos.map((photo) => (
              <div key={photo.id} className="border rounded overflow-hidden">
                <img
                  src={photo.thumbnail_url}
                  alt="CompanyCam photo"
                  className="w-full h-32 object-cover"
                />
                <div className="p-2 text-xs">
                  <p className="text-gray-500">
                    {new Date(photo.taken_at).toLocaleDateString()}
                  </p>
                  {photo.coordinates && (
                    <p className="text-green-600">GPS: Located</p>
                  )}
                  {photo.tags.length > 0 && (
                    <p className="text-blue-600">
                      Tags: {photo.tags.slice(0, 2).join(', ')}
                    </p>
                  )}
                </div>
              </div>
            ))}
          </div>
        </div>
      )}

      <div className="mt-4 p-3 bg-blue-50 rounded">
        <h5 className="font-medium text-blue-800">How it works:</h5>
        <ul className="text-sm text-blue-700 mt-1">
          <li>• Inspectors use CompanyCam mobile app on-site</li>
          <li>• Photos are automatically organized by project</li>
          <li>• GPS coordinates and timestamps are captured</li>
          <li>• Sync photos here to include in final reports</li>
        </ul>
      </div>
    </div>
  );
};

export default CompanyCamIntegration;
