import React, { useState } from 'react';

interface CompanyCamIntegrationProps {
  projectId: string;
}

const CompanyCamIntegration: React.FC<CompanyCamIntegrationProps> = ({ projectId }) => {
  const [companyCamProjectId, setCompanyCamProjectId] = useState('');
  const [syncing, setSyncing] = useState(false);
  const [lastSync, setLastSync] = useState<Date | null>(null);
  const [syncedPhotos, setSyncedPhotos] = useState(0);

  const handleSync = async () => {
    if (!companyCamProjectId.trim()) {
      alert('Please enter a CompanyCam Project ID');
      return;
    }

    setSyncing(true);

    try {
      const response = await fetch(`/api/projects/${projectId}/sync-companycam`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          companyCamProjectId: companyCamProjectId.trim()
        })
      });

      if (response.ok) {
        const result = await response.json();
        setLastSync(new Date());
        setSyncedPhotos(result.synced_photos || 0);
        alert(`Successfully synced ${result.synced_photos} photos from CompanyCam!`);
        window.location.reload();
      } else {
        const error = await response.json();
        alert(`Sync failed: ${error.error}`);
      }
    } catch (error) {
      console.error('Sync error:', error);
      alert('Failed to connect to CompanyCam. Please try again.');
    } finally {
      setSyncing(false);
    }
  };

  return (
    <div className="mt-6 bg-white rounded-lg shadow p-6">
      <h2 className="font-bold mb-4">
        CompanyCam Integration - Field Photo Sync
      </h2>

      <div className="space-y-4">
        <div className="bg-blue-50 border border-blue-200 rounded p-4">
          <h3 className="font-semibold text-blue-900 mb-2">Inspector Workflow:</h3>
          <ol className="text-sm text-blue-800 space-y-1">
            <li>1. Inspector receives project assignment</li>
            <li>2. Inspector visits property with CompanyCam mobile app</li>
            <li>3. Photos are taken and automatically GPS-tagged</li>
            <li>4. CompanyCam organizes photos by project ID</li>
            <li>5. Estimator syncs photos using the project ID below</li>
          </ol>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              CompanyCam Project ID
            </label>
            <input
              type="text"
              value={companyCamProjectId}
              onChange={(e) => setCompanyCamProjectId(e.target.value)}
              placeholder="Enter CompanyCam project ID"
              className="w-full px-3 py-2 border border-gray-300 rounded"
              disabled={syncing}
            />
          </div>

          <div className="flex flex-col justify-end">
            <button
              onClick={handleSync}
              disabled={syncing || !companyCamProjectId.trim()}
              className="px-6 py-2 rounded font-medium bg-blue-600 text-white hover:bg-blue-700 disabled:bg-gray-300"
            >
              {syncing ? 'Syncing Photos...' : 'Sync CompanyCam Photos'}
            </button>
          </div>
        </div>

        {lastSync && (
          <div className="bg-green-50 border border-green-200 rounded p-3">
            <p className="text-sm text-green-800">
              Last Sync: {lastSync.toLocaleString()}
              {syncedPhotos > 0 && ` (${syncedPhotos} photos imported)`}
            </p>
          </div>
        )}
      </div>
    </div>
  );
};

export default CompanyCamIntegration;
