import React, { useState } from 'react';
import './App.css';
import ClientsPage from './ClientsPage';

function App() {
  const [currentPage, setCurrentPage] = useState('dashboard');
  const [apiStatus, setApiStatus] = React.useState<string>('Checking...');

  React.useEffect(() => {
    fetch('/api/test')
      .then(res => res.json())
      .then((data: any) => setApiStatus(data.message))
      .catch(() => setApiStatus('API connection failed'));
  }, []);

  const renderPage = () => {
    switch(currentPage) {
      case 'clients':
        return <ClientsPage />;
      default:
        return (
          <div className="App">
            <header className="App-header">
              <h1>Roofing CRM System</h1>
              <p>Welcome to your professional roofing contractor management system!</p>

              <div style={{ 
                marginTop: '2rem', 
                padding: '1rem', 
                background: '#f0f8ff', 
                borderRadius: '8px', 
                color: '#333' 
              }}>
                <h3>System Status</h3>
                <p><strong>Frontend:</strong> Running</p>
                <p><strong>Backend API:</strong> {apiStatus}</p>
                <p><strong>Database:</strong> Connected</p>
              </div>

              <div style={{ marginTop: '2rem' }}>
                <button 
                  onClick={() => setCurrentPage('clients')}
                  style={{
                    background: '#2563eb',
                    color: 'white',
                    padding: '12px 24px',
                    border: 'none',
                    borderRadius: '8px',
                    cursor: 'pointer',
                    fontSize: '16px'
                  }}
                >
                  View Clients
                </button>
              </div>
            </header>
          </div>
        );
    }
  };

  return (
    <div>
      {currentPage !== 'dashboard' && (
        <nav style={{ padding: '1rem', background: '#f8f9fa', borderBottom: '1px solid #dee2e6' }}>
          <button 
            onClick={() => setCurrentPage('dashboard')}
            style={{ marginRight: '1rem', padding: '8px 16px', background: '#6c757d', color: 'white', border: 'none', borderRadius: '4px', cursor: 'pointer' }}
          >
            Back to Dashboard
          </button>
          <span style={{ fontWeight: 'bold' }}>Client Management</span>
        </nav>
      )}
      {renderPage()}
    </div>
  );
}

export default App;
