import React, { useState, useEffect } from "react";

const ProjectDetailsPage: React.FC<{projectId: string; onBack: () => void}> = ({ projectId, onBack }) => {
  const [project, setProject] = useState<any>(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch(`/api/projects/${projectId}`)
      .then(res => res.json())
      .then(data => {
        setProject(data.project);
        setLoading(false);
      })
      .catch(err => {
        console.error('Failed to load project:', err);
        setLoading(false);
      });
  }, [projectId]);

  if (loading) return <div className="p-8">Loading project details...</div>;
  if (!project) return <div className="p-8">Project not found</div>;

  return (
    <div className="p-8">
      <button onClick={onBack} className="bg-gray-500 text-white px-4 py-2 rounded mb-4">
        ‚Üê Back to Queue
      </button>
      <h1 className="text-2xl font-bold mb-4">Project Details</h1>
      <div className="bg-white rounded-lg shadow p-6">
        <div className="grid grid-cols-2 gap-4">
          <div>
            <p><strong>Contractor:</strong> {project.contractor.companyName}</p>
            <p><strong>Property:</strong> {project.propertyAddress}</p>
            <p><strong>Owner:</strong> {project.propertyOwnerName}</p>
          </div>
          <div>
            <p><strong>Status:</strong> {project.status}</p>
            <p><strong>Service Fee:</strong> ${project.totalCost}</p>
          </div>
        </div>
        <div className="mt-6">
          <h3 className="font-bold mb-2">File Upload</h3>
          <p className="text-gray-500">File upload functionality coming soon...</p>
        </div>
      </div>
    </div>
  );
};

export default ProjectDetailsPage;
