import React, { useState, useEffect } from 'react';

interface ProjectDetailsPageProps {
  projectId: string;
  onBack: () => void;
}

const ProjectDetailsPage: React.FC<ProjectDetailsPageProps> = ({ projectId, onBack }) => {
  const [project, setProject] = useState<any>(null);
  const [files, setFiles] = useState<any[]>([]);
  const [uploading, setUploading] = useState(false);

  useEffect(() => {
    // Load project details and files
    Promise.all([
      fetch(`/api/projects/${projectId}`).then(res => res.json()),
      fetch(`/api/projects/${projectId}/files`).then(res => res.json())
    ]).then(([projectData, filesData]) => {
      setProject(projectData.project);
      setFiles(filesData.files || []);
    }).catch(err => {
      console.error('Failed to load project details:', err);
    });
  }, [projectId]);

  const handleFileUpload = async (event: React.ChangeEvent<HTMLInputElement>) => {
    const selectedFiles = event.target.files;
    if (!selectedFiles || selectedFiles.length === 0) return;

    setUploading(true);

    for (let i = 0; i < selectedFiles.length; i++) {
      const file = selectedFiles[i];
      const formData = new FormData();
      formData.append('file', file);
      formData.append('projectId', projectId);
      formData.append('fileType', file.type.startsWith('image/') ? 'photo' : 'document');

      try {
        const response = await fetch(`/api/projects/${projectId}/files`, {
          method: 'POST',
          body: formData
        });

        if (response.ok) {
          const newFile = await response.json();
          setFiles(prev => [...prev, newFile]);
        }
      } catch (error) {
        console.error('Upload failed:', error);
        alert(`Failed to upload ${file.name}`);
      }
    }

    setUploading(false);
  };

  const generateReport = async () => {
    try {
      const response = await fetch(`/api/projects/${projectId}/generate-report`, {
        method: 'POST'
      });

      if (response.ok) {
        const blob = await response.blob();
        const url = window.URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = `inspection-report-${projectId}.pdf`;
        document.body.appendChild(a);
        a.click();
        window.URL.revokeObjectURL(url);
        document.body.removeChild(a);
      }
    } catch (error) {
      alert('Failed to generate report');
    }
  };

  if (!project) return <div className="p-8">Loading project details...</div>;

  return (
    <div className="p-8">
      <div className="flex items-center mb-6">
        <button
          onClick={onBack}
          className="bg-gray-500 text-white px-4 py-2 rounded mr-4"
        >
          ← Back to Queue
        </button>
        <h1 className="text-3xl font-bold">Project Details</h1>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Project Information */}
        <div className="bg-white rounded-lg shadow p-6">
          <h2 className="text-xl font-bold mb-4">Project Information</h2>
          <div className="space-y-2">
            <p><strong>Contractor:</strong> {project.contractor.companyName}</p>
            <p><strong>Property:</strong> {project.propertyAddress}</p>
            <p><strong>Owner:</strong> {project.propertyOwnerName}</p>
            <p><strong>Phone:</strong> {project.propertyOwnerPhone}</p>
            <p><strong>Service Fee:</strong> ${project.totalCost}</p>
            <p><strong>Status:</strong> 
              <span className={`ml-2 px-2 py-1 text-xs rounded-full ${
                project.status === 'COMPLETED' ? 'bg-green-100 text-green-800' :
                project.status === 'IN_PROGRESS' ? 'bg-blue-100 text-blue-800' :
                'bg-yellow-100 text-yellow-800'
              }`}>
                {project.status}
              </span>
            </p>
          </div>
        </div>

        {/* File Upload */}
        <div className="bg-white rounded-lg shadow p-6">
          <h2 className="text-xl font-bold mb-4">Documentation</h2>

          <div className="mb-4">
            <label className="block mb-2 font-medium">Upload Photos/Documents</label>
            <input
              type="file"
              multiple
              accept="image/*,.pdf,.doc,.docx"
              onChange={handleFileUpload}
              disabled={uploading}
              className="w-full border rounded px-3 py-2"
            />
            {uploading && <p className="text-blue-600 mt-2">Uploading files...</p>}
          </div>

          <div className="mb-4">
            <button
              onClick={generateReport}
              className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
              disabled={project.status !== 'COMPLETED'}
            >
              Generate PDF Report
            </button>
            {project.status !== 'COMPLETED' && (
              <p className="text-sm text-gray-500 mt-1">
                Complete project to generate report
              </p>
            )}
          </div>
        </div>
      </div>

      {/* Files List */}
      <div className="mt-6 bg-white rounded-lg shadow p-6">
        <h2 className="text-xl font-bold mb-4">Project Files ({files.length})</h2>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          {files.map((file) => (
            <div key={file.id} className="border rounded p-4">
              {file.fileType === 'photo' ? (
                <img
                  src={file.fileUrl}
                  alt={file.filename}
                  className="w-full h-32 object-cover rounded mb-2"
                />
              ) : (
                <div className="w-full h-32 bg-gray-100 rounded mb-2 flex items-center justify-center">
                  <span className="text-gray-500">�� {file.filename}</span>
                </div>
              )}
              <p className="text-sm font-medium">{file.filename}</p>
              <p className="text-xs text-gray-500">
                {new Date(file.uploadedAt).toLocaleDateString()}
              </p>
            </div>
          ))}
          {files.length === 0 && (
            <p className="text-gray-500 col-span-full text-center py-8">
              No files uploaded yet
            </p>
          )}
        </div>
      </div>
    </div>
  );
};

export default ProjectDetailsPage;
