import React, { useState, useEffect } from 'react';

const ProjectsPage: React.FC = () => {
  const [projects, setProjects] = useState<any[]>([]);
  const [staff, setStaff] = useState<any[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Load projects and staff data
    Promise.all([
      fetch('/api/projects').then(res => res.json()),
      fetch('/api/staff').then(res => res.json())
    ]).then(([projectsData, staffData]) => {
      setProjects(projectsData.projects);
      setStaff(staffData.staff || []);
      setLoading(false);
    }).catch(err => {
      console.error('Failed to load data:', err);
      setLoading(false);
    });
  }, []);

  const assignStaff = async (projectId: string, staffId: string, role: string) => {
    try {
      const response = await fetch(`/api/projects/${projectId}/assign`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ staffId, role })
      });

      if (response.ok) {
        // Reload projects to show updated assignments
        const updatedProjects = await fetch('/api/projects').then(res => res.json());
        setProjects(updatedProjects.projects);
        alert(`${role} assigned successfully!`);
      }
    } catch (error) {
      alert('Error assigning staff');
    }
  };

  const updateProjectStatus = async (projectId: string, newStatus: string) => {
    try {
      const response = await fetch(`/api/projects/${projectId}/status`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ status: newStatus })
      });

      if (response.ok) {
        const updatedProjects = await fetch('/api/projects').then(res => res.json());
        setProjects(updatedProjects.projects);
      }
    } catch (error) {
      alert('Error updating status');
    }
  };

  const inspectors = staff.filter(s => s.role === 'INSPECTOR');
  const estimators = staff.filter(s => s.role === 'ESTIMATOR');

  if (loading) return <div className="p-8">Loading work queue...</div>;

  return (
    <div className="p-8">
      <h1 className="text-3xl font-bold mb-6">Work Queue - Paid Projects</h1>
      <div className="bg-white rounded-lg shadow overflow-hidden">
        <table className="min-w-full">
          <thead className="bg-gray-50">
            <tr>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Project</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Inspector</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Estimator</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Status</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Actions</th>
            </tr>
          </thead>
          <tbody className="divide-y divide-gray-200">
            {projects.map((project) => (
              <tr key={project.id} className="hover:bg-gray-50">
                <td className="px-6 py-4 whitespace-nowrap">
                  <div className="font-medium">{project.contractor.companyName}</div>
                  <div className="text-sm text-gray-500">{project.propertyAddress}</div>
                  <div className="text-sm text-gray-500">Owner: {project.propertyOwnerName}</div>
                  <div className="text-sm font-medium">${project.totalCost}</div>
                </td>
                <td className="px-6 py-4 whitespace-nowrap">
                  {project.assignedInspector ? (
                    <div className="text-sm">
                      <div className="font-medium">{project.assignedInspector}</div>
                      <div className="text-green-600">Assigned</div>
                    </div>
                  ) : (
                    <select
                      className="text-sm border rounded px-2 py-1"
                      onChange={(e) => {
                        if (e.target.value) {
                          assignStaff(project.id, e.target.value, 'inspector');
                        }
                      }}
                      defaultValue=""
                    >
                      <option value="">Select Inspector</option>
                      {inspectors.map(inspector => (
                        <option key={inspector.id} value={inspector.id}>
                          {inspector.name}
                        </option>
                      ))}
                    </select>
                  )}
                </td>
                <td className="px-6 py-4 whitespace-nowrap">
                  {project.assignedEstimator ? (
                    <div className="text-sm">
                      <div className="font-medium">{project.assignedEstimator}</div>
                      <div className="text-green-600">Assigned</div>
                    </div>
                  ) : project.assignedInspector ? (
                    <select
                      className="text-sm border rounded px-2 py-1"
                      onChange={(e) => {
                        if (e.target.value) {
                          assignStaff(project.id, e.target.value, 'estimator');
                        }
                      }}
                      defaultValue=""
                    >
                      <option value="">Select Estimator</option>
                      {estimators.map(estimator => (
                        <option key={estimator.id} value={estimator.id}>
                          {estimator.name}
                        </option>
                      ))}
                    </select>
                  ) : (
                    <span className="text-gray-400">Assign Inspector First</span>
                  )}
                </td>
                <td className="px-6 py-4 whitespace-nowrap">
                  <span className={`px-2 py-1 text-xs rounded-full ${
                    project.status === 'COMPLETED' ? 'bg-green-100 text-green-800' :
                    project.status === 'IN_PROGRESS' ? 'bg-blue-100 text-blue-800' :
                    project.status === 'ASSIGNED' ? 'bg-yellow-100 text-yellow-800' :
                    'bg-gray-100 text-gray-800'
                  }`}>
                    {project.status}
                  </span>
                </td>
                <td className="px-6 py-4 whitespace-nowrap text-sm">
                  {project.status === 'ASSIGNED' && (
                    <button
                      onClick={() => updateProjectStatus(project.id, 'IN_PROGRESS')}
                      className="text-blue-600 hover:text-blue-900"
                    >
                      Start Work
                    </button>
                  )}
                  {project.status === 'IN_PROGRESS' && (
                    <button
                      onClick={() => updateProjectStatus(project.id, 'COMPLETED')}
                      className="text-green-600 hover:text-green-900"
                    >
                      Mark Complete
                    </button>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default ProjectsPage;
