import React, { useState, useEffect } from "react";
import ProjectDetailsPage from "./ProjectDetailsPage";

const ProjectsPage: React.FC = () => {
  const [projects, setProjects] = useState<any[]>([]);
  const [loading, setLoading] = useState(true);
  const [selectedProjectId, setSelectedProjectId] = useState<string | null>(null);

  useEffect(() => {
    fetch("/api/projects")
      .then(res => res.json())
      .then(data => {
        setProjects(data.projects);
        setLoading(false);
      })
      .catch(err => {
        console.error("Failed to load projects:", err);
        setLoading(false);
      });
  }, []);

  if (selectedProjectId) {
    return (
      <ProjectDetailsPage
        projectId={selectedProjectId}
        onBack={() => setSelectedProjectId(null)}
      />
    );
  }

  if (loading) return <div className="p-8">Loading work queue...</div>;

  return (
    <div className="p-8">
      <h1 className="text-3xl font-bold mb-6">Work Queue - Paid Projects</h1>
      <div className="bg-white rounded-lg shadow p-6">
        {projects.map(project => (
          <div key={project.id} className="border-b py-4 flex justify-between items-center">
            <div>
              <p><strong>Contractor:</strong> {project.contractor.companyName}</p>
              <p><strong>Property:</strong> {project.propertyAddress}</p>
              <p><strong>Owner:</strong> {project.propertyOwnerName}</p>
            </div>
            <button
              onClick={() => setSelectedProjectId(project.id)}
              className="bg-blue-600 text-white px-4 py-2 rounded"
            >
              View Details
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProjectsPage;
